def extract_all_category_ids(categories, ids=None):
    """Recursively extract all category IDs from the hierarchy"""
    if ids is None:
        ids = []

    for cat in categories:
        ids.append(cat['id'])
        if 'children' in cat:
            extract_all_category_ids(cat['children'], ids)

    return ids

def generate_icons():
    """Generate SVG icons for all categories"""
    # Load categories.json
    script_dir = os.path.dirname(os.path.abspath(__file__))
    categories_path = os.path.join(script_dir, '..', 'frontend', 'public', 'categories.json')
    output_dir = os.path.join(script_dir, '..', 'frontend', 'public', 'images', 'categories')

    with open(categories_path, 'r') as f:
        data = json.load(f)

    # Create output directory
    os.makedirs(output_dir, exist_ok=True)

    # Extract all category IDs
    all_ids = extract_all_category_ids(data['categories'])

    print(f"Found {len(all_ids)} categories")
    print(f"Icon templates available: {len(ICON_TEMPLATES)}")

    # Generate icons
    created = 0
    for cat_id in all_ids:
        if cat_id in ICON_TEMPLATES:
            output_path = os.path.join(output_dir, f"{cat_id}.svg")
            with open(output_path, 'w') as f:
                f.write(ICON_TEMPLATES[cat_id])
            print(f"✓ Created {cat_id}.svg")
            created += 1
        else:
            print(f"✗ Missing template for {cat_id}")

    print(f"\n{created}/{len(all_ids)} icons created")
    print(f"Output directory: {output_dir}")

    return created, len(all_ids)

if __name__ == '__main__':
    created, total = generate_icons()
    exit(0 if created == total else 1)
